INSERT INTO `youtube_videos` (`id`, `video_id`, `youtube_channel_id`, `youtube_category_id`, `title`, `description`, `thumnail_image_url`, `published_at`, `comment_count`, `dislike_count`, `like_count`, `favorite_count`, `view_count`, `is_related`, `tags`) VALUES (10106051,'fTUO1t9Z_3w',NULL,NULL,'【人狼J】21素村グレで人狼に狩りと思わせる作戦は裏目?【人狼ジャッジメント】','ゲーム名:人狼ジャッジメント人狼J\n実況者:ちょめ\n\n今回は中級者村です。\n占い結果も280秒がセオリーみたいです。\n市民は村のために噛まれる事が最高の貢献だと思ってます。\n\n2日目にメアリーがミカ何かある?って聞いたのは・・・\nメアリーが狩りだから狼が偽狩りを臭わすのを狙ってたのかな?\n私は素村って言うと、\n他の人の狩りの幅が狭まり透けるから狩りっぽく村って言ったら・・・\n逆に怪しまれた感じになりましたね。。。\nアンナもグレだったのに私はあえて噛まれないで残されたか・・・\n狂人のクリスに私が村側とバレてたみたいなので、\n私を狼と仕立てるやり方は凄く優秀で上手かったですね。\n狩りでも嘘で素村って言う人いるし素直に何もない。素村ですって反論しとけば良かったかな?\n\n再生リスト\nhttps://www.youtube.com/playlist?list=PLVPT_FjXs3Takkdm0-ehnEUA-deVbqq5g','https://i.ytimg.com/vi/fTUO1t9Z_3w/default.jpg','2018-04-22 15:15:00',0,0,0,0,28,1,'[\"グレ\",\"中級者\",\"人狼\",\"人狼j\",\"人狼ジャッジメント\",\"作戦\",\"実況\",\"狩人\",\"素村\",\"裏目\"]');
